mxGraphModel.add:
function(cell, value)
{
	var previous = null;

	if (value == null || isNaN(value.nodeType))
	{
		previous = cell.value.getAttribute('label');

		if (value == null)
		{
			cell.value.removeAttribute('label');
		}
		else
		{
			cell.setAttribute('label', value);
		}
	}
	else
	{
		previous = cell.value;
		cell.value = value;
	}

	return previous;
}
